addEventListener("load",()=>{const a=document.querySelector("#theme"),m=()=>{const e=document.querySelector("html"),t=e.getAttribute("data-theme");t==="dark"?(e.setAttribute("data-theme","light"),document.cookie="theme=light; max-age=315360000; path=/; SameSite"):t==="light"&&(e.setAttribute("data-theme","dark"),document.cookie="theme=dark; max-age=315360000; path=/; SameSite")};a.addEventListener("click",m)});
